import React from "react";
import "./style/index.scss";
import foto from "./img/Asset 8.svg";
import Foto2 from "./img/Dashboard.svg";
import { BrowserRouter as Router, useRouteMatch, Link } from "react-router-dom";
// import routes from "./js/routes.js";

function LinkInto({ label, to, activeOnlyWhenExact }) {
  let match = useRouteMatch({
    path: to,
    exact: activeOnlyWhenExact,
  });
  if (label == "Dashboard"){
    let foto = Foto2;
  }
  else{
    let foto = foto;
  }
  return (
    <div className={match ? "aktif" : "aktif"}>
      {match && " +"}
      <Link to={to}>{label}</Link>
    </div>
  );
}

export default class Sidebar extends React.Component {
  render() {
    return (
      <div className="sidebar text-nowrap ">
        <div className="brand-icon">
          <img src={foto} alt="prop" />
        </div>
        <ul className="select text-break">
          <li>
            <Link to="/">Dashboard</Link>
          </li>
          <li>
            <LinkInto label="Logistik" to="/Logistik" activeOnlyWhenExact={true} />
          </li>
          <li>
            <Link to="/Bencana">Bencana Alam</Link>
          </li>

          <li>
            <Link to="/Penanggulangan">Penanggulangan Bencana</Link>
          </li>
          <li>
            <Link to="/Pegawai">Pegawai BPBD</Link>
          </li>
          <li>
            <Link to="/Relawan">Relawan</Link>
          </li>
          <li>
            <Link to="/Laporan">Laporan</Link>
          </li>
        </ul>
      </div>
    );
  }
}
