import React from "react";
import "./style/index.scss";
import icoSearch from "./img/icon/IcoSearch.svg";
import icoEdit from "./img/icon/IcoEdit.svg";
import arrowNext from "./img/icon/arrow-next.svg";
import arrowPrev from "./img/icon/arrow-prev.svg";

import { Container, Row, Col } from "react-bootstrap";
import { Link } from "react-router-dom";
import Pagination from "react-js-pagination";
// import SweetAlert from "react-bootstrap-sweetalert";
import axios from "axios";

class DataSupplier extends React.Component {
  constructor() {
    super();
    this.state = {
      items: [],
      loading: true,
      query: "",
      massage: "",
      url: "",
    };
    this.cancel = "";
  }

  onChangeHandle = (event) => {
    const isi = event.target.value;
    if (isi) {
      this.setState({ query: isi, loading: true, massage: "" }, () => {
        this.onSearchData(isi);
      });
    } else {
      this.setState({ massage: "", query: isi }, () => {
        this.onGetData();
      });
    }
  };

  onGetData = async (pageNumber = 1) => {
    const url = "http://localhost:8000/api/supplier?page=" + pageNumber;
    try {
      if (this.cancel) {
        this.cancel.cancel();
      }
      this.cancel = axios.CancelToken.source();
      const response = await axios.get(url, {
        cancelToken: this.cancel.token,
      });
      this.setState({
        items: response.data,
        loading: false,
      });
    } catch (err) {}
  };

  onSearchData = (data) => {
    const search = data ? `/search?q=${data}` : ``;
    const url = `http://127.0.0.1:8000/api/supplier${search}`;
    //${pageNumber}
    if (this.cancel) {
      this.cancel.cancel();
    }
    this.cancel = axios.CancelToken.source();

    axios
      .post(url, {
        cancelToken: this.cancel.token,
      })
      .then((response) => {
        const resultNotFound = !response.data.length ? null : "Data Kosong";
        this.setState(
          {
            items: response.data,
            massage: resultNotFound,
            loading: false,
            url: url,
          },
          () => {
            console.log(this.cancel);
          }
        );
      })
      .catch((error) => {
        if (axios.isCancel(error) || error) {
          this.setState({
            loading: false,
            massage: "Failed Data, Please check",
          });
        }
      });
  };

  renderSupplierList() {
   const { data, current_page, per_page, total } = this.state.items;
   const { loading, massage } = this.state;

   if (loading) {
     this.onGetData("", "");
     return <div className="loader"></div>;
   } else if (massage) {
     return <div>{massage}</div>;
   } else if(data) {
     return (
       <div className="cardTable">
         <table className="table table-striped">
           <thead className="border-top-0">
             <tr>
               <th>Nama</th>
               <th>Alamat</th>
               <th>Opsi</th>
             </tr>
           </thead>
           <tbody>
             {data.map((item, index) => (
               <tr key={index}>
                 <td>{item.nama}</td>
                 <td>{item.alamat}</td>
                 <td>
                   <Link
                     to={`/Logistik/EditDataSupplier/${item.id}`}
                     className="btn btn-warning edit"
                   >
                     <img src={icoEdit} alt="edit" className="icoOption" />
                   </Link>
                 </td>
               </tr>
             ))}
           </tbody>
         </table>

         <nav aria-label="Page navigation example">
           <Pagination
             hideFirstLastPages
             innerClass="pagination justify-content-center"
             activePage={current_page}
             itemsCountPerPage={per_page}
             totalItemsCount={total}
             prevPageText={
               <img src={arrowPrev} alt="prev" className="icoPage" />
             }
             nextPageText={
               <img src={arrowNext} alt="next" className="icoPage" />
             }
             itemClass="page-item"
             linkClass="page-link"
             onChange={(pageNumber) => this.onGetData(pageNumber)}
           />
         </nav>
       </div>
     );
   }
   else{
      return <>{massage}</>;
   }
  }

  render() {
    const { items, query, massage, url } = this.state;
       console.log(items);
       console.log(query);
       console.log(url);
    return (
      <Container className="container-fluid">
        <div className="dataSupplier">
          <h1 className="dataTitle">Data Supplier</h1>
          <nav aria-label="breadcrumb">
            <ol className="breadcrumb">
              <li className="breadcrumb-item">
                <Link to="/">Dashboard</Link>
              </li>
              <li className="breadcrumb-item">
                <Link to="/Logistik">Logistik</Link>
              </li>
              <li className="breadcrumb-item active" aria-current="page">
                Data Supplier
              </li>
            </ol>
          </nav>
          <Row>
            <Col className="col-md-6">
              <input
                type="text"
                className="form-control search"
                placeholder="Cari..."
                id="q"
                name="q"
                value={query}
                onChange={this.onChangeHandle}
              />
              <img src={icoSearch} alt="search" className="icoSearch" />
            </Col>
            <Col className="col-md-6">
              <Link to="/Logistik/AddDataSupplier" className="btn btn-custom1">
                Tambah Data
              </Link>
            </Col>
          </Row>

          {this.renderSupplierList()}
        </div>
      </Container>
    );
  }
}

export default DataSupplier;
