import React from "react";
import "./style/index.scss";
import { Container, Row, Col } from "react-bootstrap";
import { Link } from "react-router-dom";
import { getList, addItem, deleteItem, updateItem } from './SupplierFunctions'

class AddDataSupplier extends React.Component {
  constructor() {
      super()
      this.state = {
          id: '',
          nama: '',
          alamat: '',
          items: [],
          form: { nama: "", alamat: "" }
      }

      this.onSubmit = this.onSubmit.bind(this)
  }

  componentDidMount() {
      this.getAll()
  }

  onChange = e => {
      const { name, value } = e.target;
      let form = this.state.form;
      form[name] = value;
      this.setState({ form });
      // this.setState({
      //     [e.target.name]: e.target.value
      // })
  }

  onSubmit = e => {
      e.preventDefault()
      addItem(this.state.nama, this.state.alamat).then(() => {
          this.getAll()
      })
  }

  render() {
    return (
      <Container className="container-fluid">
        <div className="addDataSupplier">
          <h1 className="dataTitle">Tambah Data Supplier</h1>
          <nav aria-label="breadcrumb">
            <ol class="breadcrumb">
              <li class="breadcrumb-item">
                <Link to="/">Dashboard</Link>
              </li>
              <li class="breadcrumb-item">
                <Link to="/Logistik">Logistik</Link>
              </li>
              <li class="breadcrumb-item">
                <Link to="/DataSupplier">Data Supplier</Link>
              </li>
              <li class="breadcrumb-item active" aria-current="page">
                Tambah Data Supplier
              </li>
            </ol>
          </nav>

          <form onSubmit={this.onSubmit} method="post">
            <Row className="nama">
              <Col className="col-md-2">
                <h3>Nama</h3>
              </Col>
              <Col className="col-md-9">
                <input type="text"
                  className="form-control"
                  id="nama"
                  name="nama"
                  value={this.state.form.nama || ''}
                  onChange={this.onChange.bind(this)} />
              </Col>
            </Row>
            <Row className="alamat">
              <Col className="col-md-2">
                <h3>Alamat</h3>
              </Col>
              <Col className="col-md-9">
                <textarea
                  name="alamat"
                  id="alamat"
                  className="form-control"
                  rows="10"
                  value={this.state.form.alamat || ''}
                  onChange={this.onChange.bind(this)}
                ></textarea>
              </Col>
            </Row>
            <Row className="submit">
              <Col className="col-md-2"></Col>
              <Col className="col-md-9">
                {/* <input
                  type="submit"
                  value="Tambah Data"
                  className="form-control"
                /> */}
                <button
                  type="submit"
                  onClick={this.onSubmit.bind(this)}
                  className="form-control"
                >
                  Tambah Data
              </button>
              </Col>
            </Row>
            <Row className="reset">
              <Col className="col-md-2"></Col>
              <Col className="col-md-9">
                <a href="" className="form-control btn btn-danger btn-reset">
                  Reset
                </a>
              </Col>
            </Row>
          </form>
        </div>
      </Container>
    );
  }
}

export default AddDataSupplier;
