import React from "react";
import "./style/index.scss";
import { Container, Row, Col } from "react-bootstrap";
import  { Doughnut }  from "react-chartjs-2";
import { Link, useRouteMatch } from "react-router-dom";

const state = {
  labels: ["January", "February", "March", "April", "May"],
  datasets: [
    {
      label: "Rainfall",
      backgroundColor: ["#B21F00", "#C9DE00", "#2FDE00", "#00A6B4", "#6800B4"],
      hoverBackgroundColor: [
        "#501800",
        "#4B5000",
        "#175000",
        "#003350",
        "#35014F",
      ],
      data: [65, 59, 80, 81, 56],
    },
  ],
};
const options = {
  legend: {
    display: true,
    position: "bottom",
  },
};

function LinkInto({ label, to, activeOnlyWhenExact }) {
  let match = useRouteMatch({
    path: to,
    exact: activeOnlyWhenExact,
  });
  return (
    <Link className={match ? "active-select" : ""} to={to}>
      {label}
    </Link>
  );
}

class Chart2 extends React.Component {
  render() {
    return (
      <Container fluid>
        <Row className="chart">
          <Col md={auto}>
            <div className="judul">Grafik Rekapitulasi Bencana</div>
            <div className="select">
              {/* <LinkInto
                label="1 Minggu"
                to="/?bulanke=1-minggu"
                activeOnlyWhenExact={true}
              />
              <LinkInto
                label="1 Bulan"
                to="/?bulanke=1-bulan"
                activeOnlyWhenExact={true}
              />
              <LinkInto
                label="6 Bulan"
                to="/?bulanke=6-bulan"
                activeOnlyWhenExact={true}
              />
              <LinkInto
                label="1 Tahun"
                to="/?bulanke=1-tahun"
                activeOnlyWhenExact={true}
              /> */}
            </div>
            <Doughnut data={state}  options={options} />
            <div className="tahun">Tahun 2020</div>
          </Col>
          {/* <Col md={1}>
            <div className="keterangan">Keterangan</div>
          </Col> */}
        </Row>
      </Container>
    );
  }
}
export default Chart2;
