import React from "react";
import "./style/index.scss";
import { Container, Row, Col } from "react-bootstrap";
import  { Doughnut }  from "react-chartjs-2";
import { Link, useRouteMatch } from "react-router-dom";

const state = {
      name: 'React',
      data: {
        datasets: [{
          data: [10, 20, 30]
        }],
        labels: [
          'Red',
          'Yellow',
          'Blue'
        ]
      }
};
const options = {
  legend: {
    display: true,
    position: "bottom",
  },
};

function LinkInto({ label, to, activeOnlyWhenExact }) {
  let match = useRouteMatch({
    path: to,
    exact: activeOnlyWhenExact,
  });
  return (
    <Link className={match ? "active-select" : ""} to={to}>
      {label}
    </Link>
  );
}

class Chart2 extends React.Component {
  render() {
    return (
      <Container fluid>
        <Row className="chart">
          <Col md={12}>
            <div className="judul">Grafik Rekapitulasi Bencana</div>
            <div className="select">
              {/* <LinkInto
                label="1 Minggu"
                to="/?bulanke=1-minggu"
                activeOnlyWhenExact={true}
              />
              <LinkInto
                label="1 Bulan"
                to="/?bulanke=1-bulan"
                activeOnlyWhenExact={true}
              />
              <LinkInto
                label="6 Bulan"
                to="/?bulanke=6-bulan"
                activeOnlyWhenExact={true}
              />
              <LinkInto
                label="1 Tahun"
                to="/?bulanke=1-tahun"
                activeOnlyWhenExact={true}
              /> */}
            </div>
            <Doughnut data={state} options={options} />
            <div className="tahun">Tahun 2020</div>
          </Col>
          {/* <Col md={1}>
            <div className="keterangan">Keterangan</div>
          </Col> */}
        </Row>
      </Container>
    );
  }
}
export default Chart2;
