//   handleInputChange(event) {
//     const target = event.target;
//     const value = target.value;
//     const name = target.name;

//     this.setState({
//       [name]: value,
//     });
//   }

const AddDataLogistik = (props) => {
  const [logistik, setLogistik] = useState({
    _id: "",
    id_kategori: "",
    nama_barang: "",
    stok: "",
    id_supplier: "",
    status: "",
    expired: "",
    suppliers: [],
    categories: [],
  });

  const onSubmit = () => {
    axios
      .post("http://localhost:8000/api/logistik", {
        // nama: logistik.nama,
        // alamat: logistik.alamat,

        id_kategori: logistik.id_kategori,
        nama_barang: logistik.nama_barang,
        stok: logistik.stok,
        id_supplier: logistik.id_supplier,
        status: logistik.status,
        expired: logistik.expired,
      })
      .then((result) => {
        console.log(result);

        props.history.push("/Logistik/DataLogistik");
      });
  };

  const { register, handleSubmit, errors } = useForm(onSubmit);

  const handleInputChange = (e) => {
    e.persist();
    setLogistik({ ...logistik, [e.target.name]: e.target.value });
  };

  // onSubmit = (e) => {
  //   e.preventDefault();

  //   axios
  //     .post("http://localhost:8000/api/logistik", {
  //       id_kategori: this.state.id_kategori,
  //       nama_barang: this.state.nama_barang,
  //       stok: this.state.stok,
  //       id_supplier: this.state.id_supplier,
  //       status: this.state.status,
  //       expired: this.state.expired,
  //     })
  //     .then((response) => {
  //       console.log(response);
  //       this.props.history.push("/Logistik/DataLogistik");
  //     });
  // };

  // componentDidMount(() => {
  //   this.getOptionSupplier();
  //   this.getOptionKategori();
  // });

  const [suppliers, categories] = useState();

  useEffect(() => {
    getOptionSupplier();
    getOptionKategori();
  }, []);

  const getOptionSupplier = () => {
    const url = "http://localhost:8000/api/supplier/all";
    const response = axios.get(url);
    this.setState(
      {
        suppliers: response.data,
      },
      () => {
        console.log(suppliers);
      }
    );
  };

  const getOptionKategori = () => {
    const url = "http://localhost:8000/api/kategori";
    const response = axios.get(url);
    this.setState(
      {
        categories: response.data,
      },
      () => {
        console.log(categories);
      }
    );
  };

  // render() {
  // const [ suppliers, categories ] = useState();

  return (
    <Container className="container-fluid">
      <div className="addDataLogistik">
        <h1 className="dataTitle">Tambah Data Logistik</h1>
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item">
              <Link to="/">Dashboard</Link>
            </li>
            <li className="breadcrumb-item">
              <Link to="/Logistik">Logistik</Link>
            </li>
            <li className="breadcrumb-item">
              <Link to="/Logistik/DataLogistik">Data Logistik</Link>
            </li>
            <li className="breadcrumb-item active" aria-current="page">
              Tambah Data Logistik
            </li>
          </ol>
        </nav>

        <form onSubmit={handleSubmit(onSubmit)} method="post">
          <Row className="nama">
            <Col className="col-md-2">
              <h3>Nama</h3>
            </Col>
            <Col className="col-md-9">
              <input
                type="text"
                // className="form-control"
                className={
                  errors?.nama_barang
                    ? "is-invalid form-control"
                    : "form-control"
                }
                id="nama_barang"
                name="nama_barang"
                ref={register({ required: true, maxLength: 45 })}
                value={logistik.nama_barang}
                onChange={handleInputChange}
              />
              {errors.nama_barang && (
                <div className="invalid-feedback">Nama tidak boleh kosong</div>
              )}
            </Col>
          </Row>

          <Row className="supplier">
            <Col className="col-md-2">
              <h3>Supplier</h3>
            </Col>
            <Col className="col-md-9">
              <select
                name="id_supplier"
                id="id_supplier"
                ref={register({ required: true })}
                value={logistik.id_supplier}
                onChange={handleInputChange}
                // className="form-control"
                className={
                  errors?.id_supplier
                    ? "is-invalid form-control"
                    : "form-control"
                }
              >
                <option value="#"> - Pilih Supplier -</option>
                {suppliers.map((item, supplier) => (
                  <option key={supplier} value={item.id}>
                    {item.nama}
                  </option>
                ))}
              </select>
              {errors.id_supplier && (
                <div className="invalid-feedback">
                  Supplier tidak boleh kosong
                </div>
              )}
            </Col>
          </Row>

          <Row className="kategori">
            <Col className="col-md-2">
              <h3>Kategori</h3>
            </Col>
            <Col className="col-md-3">
              <select
                name="id_kategori"
                id="id_kategori"
                ref={register({ required: true })}
                value={logistik.id_kategori}
                onChange={handleInputChange}
                // className="form-control"
                className={
                  errors?.id_kategori
                    ? "is-invalid form-control"
                    : "form-control"
                }
              >
                <option value="#"> - Pilih Kategori -</option>
                {categories.map((item, kategori) => (
                  <option key={kategori} value={item.id}>
                    {item.jenis_kategori}
                  </option>
                ))}
              </select>
              {errors.id_kategori && (
                <div className="invalid-feedback">
                  Kategori tidak boleh kosong
                </div>
              )}
            </Col>
            <Col className="col-md-1 offset-1">
              <h3>Stok</h3>
            </Col>
            <Col className="col-md-4">
              <input
                type="text"
                // className="form-control"
                className={
                  errors?.stok ? "is-invalid form-control" : "form-control"
                }
                id="stok"
                name="stok"
                ref={register({ required: true, maxLength: 20 })}
                value={logistik.stok}
                onChange={handleInputChange}
              />
              {errors.stok && (
                <div className="invalid-feedback">Stok tidak boleh kosong</div>
              )}
            </Col>
          </Row>

          <Row className="expired">
            <Col className="col-md-2">
              <h3>Expired</h3>
            </Col>
            <Col className="col-md-3">
              <input
                type="date"
                // className="form-control"
                className={
                  errors?.expired ? "is-invalid form-control" : "form-control"
                }
                name="expired"
                id="expired"
                ref={register({ required: true })}
                value={logistik.expired}
                onChange={handleInputChange}
              />
              {errors.expired && (
                <div className="invalid-feedback">
                  Expired tidak boleh kosong
                </div>
              )}
            </Col>
            <Col className="col-md-2 kondisi">
              <h3>Kondisi</h3>
            </Col>
            <Col className="col-md-4">
              <select
                name="status"
                id="status"
                ref={register({ required: true })}
                value={logistik.status}
                onChange={handleInputChange}
                // className="form-control"
                className={
                  errors?.status ? "is-invalid form-control" : "form-control"
                }
              >
                <option value="#"> - Pilih Kondisi Barang -</option>
                <option value="baik"> Baik </option>
                <option value="rusak"> Rusak </option>
              </select>
              {errors.status && (
                <div className="invalid-feedback">
                  Status tidak boleh kosong
                </div>
              )}
            </Col>
          </Row>

          <Row className="submit">
            <Col className="col-md-2"></Col>
            <Col className="col-md-9">
              <input
                type="submit"
                // onClick={this.onSubmit.bind(this)}
                value="Tambah Data"
                className="form-control"
              />
            </Col>
          </Row>
          <Row className="reset">
            <Col className="col-md-2"></Col>
            <Col className="col-md-9">
              <button className="form-control btn btn-danger btn-reset">
                Reset
              </button>
            </Col>
          </Row>
        </form>
      </div>
    </Container>
  );
  // }
};

export default withRouter(AddDataLogistik);
