import React from "react";
import "./component/style/body.scss";
import "./component/style/index.scss";
import Chart from "./component/chart";
import Welcome from "./component/welcome";
import Sidebar from "./component/sidebar";
import icon1 from "./component/img/Dashboard.svg";
import profile from "./component/img/profile-exam.jpg";
// Logistik
import Logistik from "./component/logistik";
import DataLogistik from "./component/dataLogistik";
import AddDataLogistik from "./component/addDataLogistik";
import EditDataLogistik from "./component/editDataLogistik";
import DataSupplier from "./component/dataSupplier";
import AddDataSupplier from "./component/addDataSupplier";
import EditDataSupplier from "./component/editDataSupplier";

import { Row, Container, Col, Nav } from "react-bootstrap";
import { BrowserRouter as Router, Switch, Route, Link, useRouteMatch } from "react-router-dom";


const routes = [{
        path: "/",
        exact: true,
        sidebar: () => < img className = "icon"
        src = { icon1 }
        alt = "icon" > < /img>,
        main: () => < Dashboard / > ,
        title: () => < div > Dashboard < /div>,
    },
    {
        path: "/Logistik",
        sidebar: () => < div > bubblegum! < /div>,
        main: () => < Logistik / > ,
        title: () => < div > Logistik < /div>,
    },
    {
        path: "/Bencana",
        sidebar: () => < div > shoelaces! < /div>,
        main: () => < h2 > Ini Nanti Bencana Alam < /h2>,
        title: () => < div > Bencana Alam < /div>,
    },
    {
        path: "/Penanggulangan",
        sidebar: () => < h2 > asdsa < /h2>,
        main: () => < h2 > Ini Nanti Data Penangulangan < /h2>,
        title: () => < div > Penanggulangan Bencana < /div>,
    },
    {
        path: "/Pegawai",
        sidebar: () => < h2 > asdsa < /h2>,
        main: () => < h2 > Ini Nanti Data Pegawai < /h2>,
        title: () => < div > Pegawai < /div>,
    },
    {
        path: "/Relawan",
        sidebar: () => < h2 > asdsa < /h2>,
        main: () => < h2 > Ini Nanti Data Relawan < /h2>,
        title: () => < div > Relawan < /div>,
    },
    {
        path: "/Laporan",
        sidebar: () => < h2 > asdsa < /h2>,
        main: () => < h2 > Ini Nanti Data Laporan < /h2>,
        title: () => < div > Laporan < /div>,
    },
];

export default function App() {
    return ( <
        Router >
        <
        Container fluid >
        <
        Row >
        <
        Col className = "col-md-2 bg-white position-sticky" >
        <
        Sidebar / >
        <
        /Col>{" "} <
        Col className = "col-md-10" >
        <
        Navbar > < /Navbar> <
        Switch > {
            routes.map((route, index) => ( <
                Route key = { index }
                path = { route.path }
                exact = { route.exact }
                children = { < route.main / > }
                />
            ))
        } <
        /Switch> <
        /Col>{" "} <
        /Row>{" "} <
        /Container>{" "} <
        /Router>
    );
}

function LinkInto({ to }) {
    let match = useRouteMatch({
        path: to,
        exact: routes.exact,
    });

    return ( <
        div className = { match ? "aktif" : "" } > {
            match && < path class = "block" > < /path>} <
            Link to = { routes.path } > { routes.title } < /Link> <
            /div>
        );
    }

    function Dashboard() {
        return ( <
            div className = "main" >
            <
            Welcome > < /Welcome> <
            Chart / >
            <
            /div>
        );
    }

    function Navbar() {
        return ( <
            Nav className = "justify-content-between"
            activeKey = "/home"
            onSelect = {
                (selectedKey) => alert(`selected ${selectedKey}`) } >
            <
            Nav.Item >
            <
            Nav.Link className = "judul"
            href = "" >
            <
            Switch > {
                routes.map((route, index) => ( <
                    Route key = { index }
                    path = { route.path }
                    exact = { route.exact }
                    children = { < route.title / > }
                    />
                ))
            } <
            /Switch> <
            /Nav.Link> <
            /Nav.Item> <
            Nav.Item >
            <
            Nav.Link eventKey = "link-1" >
            <
            img className = "profile-icon"
            src = { profile }
            /> <
            /Nav.Link> <
            /Nav.Item> <
            /Nav>
        );
    }
