import React from "react";
import Data from "./js/dataTable";
import {
  useLocation
} from "react-router-dom";
// import Data from "./js/dataTable.js";
// import icoEdit from "./img/icon/IcoEdit.svg";
// import icoDelete from "./img/icon/IcoDelete.svg";

// import { Table } from "react-bootstrap";
// import { Link } from "react-router-dom";


export default class Tabel extends React.Component {

  constructor(props) {
    super(props); //since we are extending class Table so we have to use super in order to override Component class constructor
    this.state = Data;

  }

  render() {
    let query = useQuery();
    return (
        <Child name={query.get("value")} />
    );
  }
}
function useQuery() {
  return new URLSearchParams(useLocation().search);
}
function Child({name}){
  return (
    <div>
      {name ? (
        <h3>
          The <code>name</code> in the query string is &quot;{name}
          &quot;
        </h3>
      ) : (
        <h3>There is no name in the query string</h3>
      )}
    </div>
  );
}

