import React from "react";
import "./style/index.scss";
import { Container, Row, Col } from "react-bootstrap";
import Line from "react-chartjs-2";
import {Link,userRouteMatch} from "react-router-dom";
import './js/isi.js'

const state = {
  labels: ["January", "February", "March", "April", "May"],
  datasets: [
    {
      label: "Rainfall",
      fill: false,
      lineTension: 1,
      backgroundColor: "rgba(75,192,192,1)",
      borderColor: "rgba(0,0,0,1)",
      borderWidth: 0.5,
      data: [65, 59, 80, 81, 56],
    },
  ],
};
function LinkInto({ label, to, activeOnlyWhenExact }) {
  let match = useRouteMatch({
    path: to,
    exact: activeOnlyWhenExact,
  });
  return (
      <Link className={match ? "aktif" : ""} to={to}>
        {label}
      </Link>
  );
}

class Chart extends React.Component {
  constructor(props) {
    super(props);
        }
  
  render() {
    return (
      <Container className="container-fluid">
        <Row className="chart">
          <Col className="col-md-10 ">
            <div className="judul">Grafik Rekapitulasi Bencana</div>
            <div className="select">
              <a className="active-select" href="">
                1 Minggu
              </a>
                    
            <LinkInto
              label="Logistik"
              to="/Logistik"
              activeOnlyWhenExact={true}
            />
              <a href="">6 Bulan</a>
              <a href="">1 Tahun</a>
            </div>
            <Line
              data={state}
              options={{
                title: {
                  display: false,
                  text: "Average Rainfall per month",
                  fontSize: 20,
                },
                legend: {
                  display: false,
                  position: "right",
                },
              }}
            />
          </Col>
        </Row>
      </Container>
    );
  }
}
export default Chart;
